	{
		return $this->loadByField(
			"previewUrl",
			$previewUrl
		);
	}
	public function relatedSchools(): SchoolsSet
	{
		$ids = [$this->getSchoolLink()];
		$collection = new SchoolsSet();
		$collection->loadFromIds($ids);
